pragma solidity ^0.4.22;
contract HelloWorld {
    bytes32 greeting;
    constructor() public{
        greeting="Hi!";
    }
    
    function setGreeting(bytes32 _greeting) public {
        greeting = _greeting;
    }
    
    function setGreeting2() public {
        greeting = "why2";
    }
    
    function setGreeting3() public {
        greeting = "why3";
    }
    
    function getGreeting() public view returns (bytes32){
        return greeting;
    }

    // 이더 송금 거래
    function setTransfer(address receiverAddress) public payable {
        receiverAddress.transfer(msg.value);
    }


}
